mapper.insert:
INSERT INTO TB_ATTD_INFO (

) VALUES (
      #{attdSn}
    , #{userId}
    , DATE_FORMAT(NOW(), '%Y%m%d')
    , #{userNm}
    , #{telNo}
    , #{sexCd}
    , #{deviceTypeNm}
    , #{content}
    , 'N'
    , #{useFcltCd}
    , NULL
    , NOW()
)

mapper.update:
UPDATE TB_ATTD_INFO
   SET MOD_DT = NOW()
     , CONTENT = #{content}
     , USER_NM = #{userNm}
 WHERE ATTD_SN = #{attdSn}

mapper.delete:
UPDATE TB_ATTD_INFO
   SET DEL_YN = 'Y'
 WHERE ATTD_SN = #{attdSn}
